mapper.select:
SELECT row_number() over(ORDER BY clientRegDate) as clientIdx, c.* FROM client c
AND clientId like CONCAT('%', #{keyword}, '%')
AND clientName like CONCAT('%', #{keyword}, '%')
AND gradeId = #{keyword}
ORDER BY clientIdx DESC LIMIT #{startList}, #{listSize}

SELECT clientEmail, clientEmailCheck
FROM client
WHERE clientId = #{clientId}

SELECT *
FROM client


		AND clientEmail = #{clientEmail}


		AND clientNum = #{clientNum}


		AND clientId = #{clientId}


AND clientName = #{clientName}
