mapper.select:
SELECT optionColor, optionSize, stockCnt
FROM option_info
WHERE productId=#{ productId }

mapper.update:
UPDATE product
SET productName=#{ productName }, productContent=#{ productContent }
WHERE productId=#{ productId }

UPDATE product_price
SET productPrimeCost=#{ productPrimeCost } 
WHERE productId=#{ product.productId }

UPDATE product
SET productSell=#{ productSell }
WHERE productId=#{ productId }

UPDATE option_info SET (productId, optionColor, optionSize, stockCnt) VALUES
(#{ option.productId }, #{ option.optionColor }, #{ option.optionSize }, #{ option.stockCnt })
WHERE productId=#{ productId }

mapper.select:
SELECT p.*, CONCAT(pm.imageDir, pm.imageName) as mainImage, pr.productPrice FROM product p LEFT JOIN product_image pm ON (p.productId = pm.imageBy AND pm.imageStatus = '대표') LEFT JOIN product_price pr ON (p.productId = pr.productId) ORDER BY
p.productCnt DESC, p.productView DESC p.productRegDate DESC
LIMIT 0,8;

SELECT clientMarkList FROM client WHERE clientId=#{ clientId } AND clientMarkList LIKE CONCAT('%',
#{ product },
'%');
